INSERT INTO `wp_wc_admin_notes` VALUES("1", "wc-refund-returns-page", "info", "en_US", "Setup a Refund and Returns Policy page to boost your store\'s credibility.", "We have created a sample draft Refund and Returns Policy page for you. Please have a look and update it to fit your store.", "{}", "unactioned", "woocommerce-core", "2025-03-25 12:01:01", NULL, "0", "plain", "", "0", "1", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("4", "your-first-product", "info", "en_US", "Your first product", "That’s huge! You’re well on your way to building a successful online store — now it’s time to think about how you’ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("5", "wc-admin-optimizing-the-checkout-flow", "info", "en_US", "Optimizing the checkout flow", "It’s crucial to get your store’s checkout as smooth as possible to avoid losing sales. Let’s take a look at how you can optimize the checkout experience for your shoppers.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("6", "wc-payments-qualitative-feedback", "info", "en_US", "WooPayments setup - let us know what you think", "Congrats on enabling WooPayments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("7", "share-your-feedback-on-paypal", "info", "en_US", "Share your feedback on PayPal", "Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("9", "wc-subscriptions-security-update-3-0-15", "info", "en_US", "WooCommerce Subscriptions security update!", "We recently released an important security update to WooCommerce Subscriptions. To ensure your site’s data is protected, please upgrade <strong>WooCommerce Subscriptions to version 3.0.15</strong> or later.<br /><br />Click the button below to view and update to the latest Subscriptions version, or log in to <a href=\"https://woocommerce.com/my-dashboard\">WooCommerce.com Dashboard</a> and navigate to your <strong>Downloads</strong> page.<br /><br />We recommend always using the latest version of WooCommerce Subscriptions, and other software running on your site, to ensure maximum security.<br /><br />If you have any questions we are here to help — just <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">open a ticket</a>.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("10", "woocommerce-core-update-5-4-0", "info", "en_US", "Update to WooCommerce 5.4.1 now", "WooCommerce 5.4.1 addresses a checkout issue discovered in WooCommerce 5.4. We recommend upgrading to WooCommerce 5.4.1 as soon as possible.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("13", "ppxo-pps-upgrade-paypal-payments-1", "info", "en_US", "Get the latest PayPal extension for WooCommerce", "Heads up! There’s a new PayPal on the block!<br /><br />Now is a great time to upgrade to our latest <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal extension</a> to continue to receive support and updates with PayPal.<br /><br />Get access to a full suite of PayPal payment methods, extensive currency and country coverage, and pay later options with the all-new PayPal extension for WooCommerce.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("14", "ppxo-pps-upgrade-paypal-payments-2", "info", "en_US", "Upgrade your PayPal experience!", "Get access to a full suite of PayPal payment methods, extensive currency and country coverage, offer subscription and recurring payments, and the new PayPal pay later options.<br /><br />Start using our <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">latest PayPal today</a> to continue to receive support and updates.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("15", "woocommerce-core-sqli-july-2021-need-to-update", "update", "en_US", "Action required: Critical vulnerabilities in WooCommerce", "In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("16", "woocommerce-blocks-sqli-july-2021-need-to-update", "update", "en_US", "Action required: Critical vulnerabilities in WooCommerce Blocks", "In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("17", "woocommerce-core-sqli-july-2021-store-patched", "update", "en_US", "Solved: Critical vulnerabilities patched in WooCommerce", "In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("18", "woocommerce-blocks-sqli-july-2021-store-patched", "update", "en_US", "Solved: Critical vulnerabilities patched in WooCommerce Blocks", "In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("20", "woocommerce-core-paypal-march-2022-updated", "update", "en_US", "Security auto-update of WooCommerce", "<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy PayPal Standard security updates for stores running WooCommerce (version 3.5 to 6.3). It’s recommended to disable PayPal Standard, and use <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal Payments</a> to accept PayPal.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("21", "woocommerce-core-paypal-march-2022-updated-nopp", "update", "en_US", "Security auto-update of WooCommerce", "<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy security updates related to PayPal Standard payment gateway for stores running WooCommerce (version 3.5 to 6.3).", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("23", "store_setup_survey_survey_q2_2022", "survey", "en_US", "How is your store setup going?", "Our goal is to make sure you have all the right tools to start setting up your store in the smoothest way possible.\r\nWe’d love to know if we hit our mark and how we can improve. To collect your thoughts, we made a 2-minute survey.", "{}", "unactioned", "woocommerce.com", "2025-04-01 16:08:37", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("24", "needs-update-eway-payment-gateway-rin-2022-12-20", "update", "en_US", "Security vulnerability patched in WooCommerce Eway Gateway", "In response to a potential vulnerability identified in WooCommerce Eway Gateway versions 3.1.0 to 3.5.0, we’ve worked to deploy security fixes and have released an updated version.\r\nNo external exploits have been detected, but we recommend you update to your latest supported version 3.1.26, 3.2.3, 3.3.1, 3.4.6, or 3.5.1", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("25", "updated-eway-payment-gateway-rin-2022-12-20", "update", "en_US", "WooCommerce Eway Gateway has been automatically updated", "Your store is now running the latest secure version of WooCommerce Eway Gateway. We worked with the WordPress Plugins team to deploy a software update to stores running WooCommerce Eway Gateway (versions 3.1.0 to 3.5.0) in response to a security vulnerability that was discovered.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("26", "ecomm-wc-navigation-survey-2023", "info", "en_US", "Navigating WooCommerce on WordPress.com", "We are improving the WooCommerce navigation on WordPress.com and would love your help to make it better! Please share your experience with us in this 2-minute survey.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("27", "woopay-beta-merchantrecruitment-04MAY23", "info", "en_US", "Increase conversions with WooPay — our fastest checkout yet", "WooPay, a new express checkout feature built into WooCommerce Payments, is now available —and we’re inviting you to be one of the first to try it. \r\n<br><br>\r\nBoost conversions by offering your customers a simple, secure way to pay with a single click.\r\n<br><br>\r\nGet started in seconds.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("28", "woocommerce-wcpay-march-2023-update-needed", "update", "en_US", "Action required: Security update for WooCommerce Payments", "<strong>Your store requires a security update for WooCommerce Payments</strong>. Please update to the latest version of WooCommerce Payments immediately to address a potential vulnerability discovered on March 22. For more information on how to update, visit this WooCommerce Developer Blog Post.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("30", "woocommerce-WCPreOrders-april-2023-update-needed", "update", "en_US", "Action required: Security update of WooCommerce Pre-Orders extension", "<strong>Your store requires a security update for the WooCommerce Pre-Orders extension</strong>. Please update the WooCommerce Pre-Orders extension immediately to address a potential vulnerability discovered on April 11.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
