INSERT INTO `wp_wc_admin_notes` VALUES("43", "woocommerce-WCReturnsWarranty-June-2023-updated-needed", "update", "en_US", "Action required: Security update of WooCommerce Returns and Warranty Requests extension", "<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("44", "woocommerce-WCOPC-June-2023-updated-needed", "update", "en_US", "Action required: Security update of WooCommerce One Page Checkout", "<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("45", "woocommerce-WCGC-July-2023-update-needed", "update", "en_US", "Action required: Security update of WooCommerce GoCardless Extension", "<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("46", "woocommerce-shipping-fedex-api-outage-2023-07-16", "warning", "en_US", "Scheduled FedEx API outage — July 2023", "On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("47", "wcship-2023-07-hazmat-update-needed", "update", "en_US", "Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension", "<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("48", "woocommerce-WCStripe-Aug-2023-update-needed", "update", "en_US", "Action required: Security update for WooCommerce Stripe plugin", "<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("49", "woocommerce-WCStripe-Aug-2023-security-updated", "update", "en_US", "Security update of WooCommerce Stripe plugin", "<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("50", "woocommerce-WooPayments-Aug-2023-security-updated", "update", "en_US", "Security update of WooPayments (WooCommerce Payments) plugin", "<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("52", "woocommerce-usermeta-Sept2023-productvendors", "update", "en_US", "Your store requires a security update", "<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("53", "woocommerce-STRIPE-Oct-2023-update-needed", "update", "en_US", "Action required: Security update for WooCommerce Stripe Gateway", "<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("56", "sirt-woo-2024-11-xss-admin", "update", "en_US", "An important update is required for WooCommerce", "This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("58", "paypal-braintree-sdk-depreciation-2025-02-20", "update", "en_US", "Update required for your PayPal Braintree extension", "To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.", "{}", "pending", "woocommerce.com", "2025-03-25 12:01:03", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("61", "wc-admin-mobile-app", "info", "en_US", "Install Woo mobile app", "Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.", "{}", "unactioned", "woocommerce-admin", "2025-03-27 12:19:24", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("62", "wc-admin-customizing-product-catalog", "info", "en_US", "How to customize your product catalog", "You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.", "{}", "unactioned", "woocommerce-admin", "2025-03-28 21:57:11", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("63", "wc-admin-onboarding-payments-reminder", "info", "en_US", "Start accepting payments on your store!", "Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.", "{}", "unactioned", "woocommerce-admin", "2025-03-30 12:00:59", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("65", "wayflyer_bnpl_q4_2021", "marketing", "en_US", "Grow your business with funding through Wayflyer", "Fast, flexible financing to boost cash flow and help your business grow – one fee, no interest rates, penalties, equity, or personal guarantees. Based on your store’s performance, Wayflyer provides funding and analytical insights to invest in your business.", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("66", "wc_shipping_mobile_app_usps_q4_2021", "marketing", "en_US", "Print and manage your shipping labels with WooCommerce Shipping and the WooCommerce Mobile App", "Save time by printing, purchasing, refunding, and tracking shipping labels generated by <a href=\"https://woocommerce.com/woocommerce-shipping/\">WooCommerce Shipping</a> – all directly from your mobile device!", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("67", "google_listings_and_ads_install", "marketing", "en_US", "Drive traffic and sales with Google", "Reach online shoppers to drive traffic and sales for your store by showcasing products across Google, for free or with ads.", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("68", "wcpay-promo-2020-11", "marketing", "en_US", "wcpay-promo-2020-11", "wcpay-promo-2020-11", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
