INSERT INTO `wp_wc_admin_notes` VALUES("70", "habit-moment-survey", "marketing", "en_US", "We’re all ears! Share your experience so far with WooCommerce", "We’d love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("71", "pinterest_03_2022_update", "marketing", "en_US", "Your Pinterest for WooCommerce plugin is out of date!", "Update to the latest version of Pinterest for WooCommerce to continue using this plugin and keep your store connected with Pinterest. To update, visit <strong>Plugins &gt; Installed Plugins</strong>, and click on “update now” under Pinterest for WooCommerce.", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("72", "tap_to_pay_iphone_q2_2023_no_wcpay", "marketing", "en_US", "Accept in-person contactless payments on your iPhone", "Tap to Pay on iPhone and WooCommerce Payments is quick, secure, and simple to set up — no extra terminals or card readers are needed. Accept contactless debit and credit cards, Apple Pay, and other NFC digital wallets in person.", "{}", "unactioned", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("73", "woocommerce-WCSubscriptions-June-2023-updated-needed", "marketing", "en_US", "Action required: Security update of WooCommerce Subscriptions", "<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("74", "avalara_q3-2023_noAvaTax", "marketing", "en_US", "Automatically calculate VAT in real time", "Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("75", "amazon-mcf-reviews-2023-12-07", "marketing", "en_US", "Enjoying Amazon MCF for WooCommerce?", "We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("76", "remove_estimated_deposits_2024", "marketing", "en_US", "Estimated deposits are going away", "To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("77", "woocommerce-analytics-order-attribution-promotions-december-2024", "marketing", "en_US", "Discover what drives your sales", "Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.", "{}", "pending", "woocommerce.com", "2025-04-01 19:48:04", NULL, "0", "plain", "", "0", "0", "info");
